package Basic.string;

/**
 * 392. 判断子序列
 * 给定字符串 s 和 t ，判断 s 是否为 t 的子序列。
 *
 * 你可以认为 s 和 t 中仅包含英文小写字母。字符串 t 可能会很长（长度 ~= 500,000），而 s 是个短字符串（长度 <=100）。
 *
 * 字符串的一个子序列是原始字符串删除一些（也可以不删除）字符而不改变剩余字符相对位置形成的新字符串。（例如，"ace"是"abcde"的一个子序列，而"aec"不是）。
 *
 * 示例 1:
 * s = "abc", t = "ahbgdc"
 *
 * 返回 true.
 *
 * 示例 2:
 * s = "axc", t = "ahbgdc"
 *
 * 返回 false.
 */
public class LeetCode392 {
    public boolean isSubsequence(String s, String t) {
        if (t == null || s == null) {
            return false;
        }
        if (t.isEmpty() && s.isEmpty()) {
            return true;
        }

        final int targetLength = t.length();
        final int slength = s.length();
        int sIndex = 0;
        for (int i = 0; i < targetLength; i++) {
            /// t 串太长，在中间就已经将 s 串全部包含
            if (sIndex == slength) {
                return true;
            }
            if (t.charAt(i) == s.charAt(sIndex)) {
                sIndex++;
            }
        }
        /// t 串到结尾才把 s 串全部包含完
        return sIndex == slength;
    }

    public static void main(String[] args) {
        LeetCode392 leetCode392 = new LeetCode392();
        boolean res = leetCode392.isSubsequence("abc", "ahbgdc");
        res = leetCode392.isSubsequence("abc", "");     // false
        res = leetCode392.isSubsequence("", "");        // true
        res = leetCode392.isSubsequence("axc", "ahbgdc");
        res = leetCode392.isSubsequence("leeeeetcode", "");
        System.out.println(res);
    }
}
